"use strict";var user="David";d3.select("#user .user-info .name").text(user);var navTitle=document.querySelectorAll(".nav-title");function openNavList(){this.classList.toggle("open")}navTitle.forEach(function(n){n.addEventListener("click",openNavList),n.addEventListener("click",changeTab_main)});var navTags=document.querySelectorAll(".nav-tag");function navTagAction(){navTags.forEach(function(n){n.classList.remove("active")}),this.classList.add("active")}navTags.forEach(function(n){n.addEventListener("click",navTagAction),n.addEventListener("click",changeTab_sub)});var nowTab=document.querySelector(".nowTab"),nowTab_main=nowTab.querySelector(".mainTab span"),nowTab_sub=nowTab.querySelector(".subTab span");function changeTab_main(){nowTab_main.innerText=this.innerText}function changeTab_sub(){nowTab_sub.innerText=this.innerText}